var express = require('express');
var router = express.Router();

var org_service = require('../services/demo_service');
var app_utils = require('../../common/utils/app_utils');

/**
 * 机构列表
 */
router.get('/list.do', async function (req, res, next) {
    try {
        let result = await org_service.pagingOrgList(req.query);
        app_utils.respJsonData(res, result);
    } catch (error) {
        app_utils.respMsg(res, false, 4000, '查询机构列表数据异常', null, error);
    }
});

module.exports = router;